import Dashboard from "../../layouts/Dashboard/Dashboard";
import { getAll, setDietStatus } from "../../routes/diets/diet.routes";
import Dropdown from "../../components/Dropdown/Dropdown";
import styles from "./General_styles/Dietas.module.css";
import favicon from "./images/favicon.svg";
import { useNavigate } from "react-router";
import caloriesIcon from "./images/calories.svg";
import grasas from "./images/grasas.svg";
import carbohidrato from "./images/carbohidrato.svg";
import proteina from "./images/proteina.svg";
import notFavicon from "./images/notFavico.svg";
import { useEffect, useState, useContext } from "react";
import { MessagesContext } from "../../layouts/Messages/Messages";
import { AppContext } from "../../App";
import { AgregarDieta } from "./AgregarDieta";

function Dietas() {
	const { addStaticMsg } = useContext(MessagesContext);
	const { user } = useContext(AppContext);

	const navigate = useNavigate();

	const [isOpenAgregar, setIsOpenAgregar] = useState(false);

	const [caloriasOpen, setCaloriasOpen] = useState(false);
	const [ingredientFilter, setIngredientFilter] = useState("%");
	const [calorieFilter, setCalorieFilter] = useState(0);

	const [top3, setTop3] = useState<any>([]);
	const [calories, setCalories] = useState<any>([]);
	const [diets, setDiets] = useState<any>([]);
	const [isFav, setFavs] = useState<any>([]);

	const filterCalories = (filter: number) => {
		setCaloriasOpen(false);
		setCalorieFilter(filter);
	};

	const macrosSum = (macros: any): number[] => {
		const carbs = Number.parseInt(
			macros.carbohidratos[0].split(0, macros.carbohidratos[0].length - 1)
		);
		const proteins = Number.parseInt(
			macros.proteina[0].split(0, macros.proteina[0].length - 1)
		);
		const fat = Number.parseInt(
			macros.grasas[0].split(0, macros.grasas[0].length - 1)
		);

		return [carbs, proteins, fat, carbs + fat + proteins];
	};

	const setDietStatusController = (dietId: string): void => {
		const doFetch = async (): Promise<void> => {
			if (isFav.indexOf(dietId) !== -1) {
				await setDietStatus(false, dietId);
			} else {
				await setDietStatus(true, dietId);
			}

			getAllController();
		};

		void doFetch();
	};

	const getAllController = (): void => {
		const doFetch = async (): Promise<void> => {
			const resData = await getAll(calorieFilter.toString(), ingredientFilter);

			if (resData === null) {
				addStaticMsg("Error al obtener las dietas", "danger");
				return;
			}

			if (resData.msg !== "") {
				addStaticMsg(resData.msg, "danger");
				return;
			}

			const data = resData.data;
            console.log()
			setTop3(data.top3);
			setCalories(data.calories);
			setDiets(data.diets);
			setFavs(data.favs);
		};
		void doFetch();
	};

	useEffect(() => {
		getAllController();
	}, [calorieFilter, ingredientFilter]);

	return (
		<Dashboard>
			<div className={styles.layout}>
				{user?.role === "Administrador" && (
					<div className={styles.agregar}>
						<button
							onClick={(e) => {
								setIsOpenAgregar(true);
							}}
						>
							Agregar dieta
						</button>
						<AgregarDieta
							isOpen={isOpenAgregar}
							setIsOpen={setIsOpenAgregar}
						></AgregarDieta>
					</div>
				)}

				<div className={styles.dietas}>
					<h1> Dietas favoritas </h1>

					{/* <section>
						{top3.length === 0 && (
							<h2>No hay dietas favoritas registradas</h2>
						)}
						{
							top3.length > 0 &&
							top3.map((element: any, key: any) => {
								const macros = macrosSum(JSON.parse(element.macros));

								return (
									<article key={key} className={styles.dieta_favorita}>
										<div className={styles.titulo}>
											<h2
												onClick={(e) =>
													navigate(`/dietas/info?dietId=${element.id}`)
												}
											>
												Dieta {element.name}
											</h2>
											<img
												className={styles.favicon}
												onClick={(e) => {
													setDietStatusController(element.id);
												}}
												src={favicon}
												alt="Icono favoritos"
											/>
										</div>
										<div
											className={styles.calorias}
											onClick={(e) =>
												navigate(`/dietas/info?dietId=${element.id}`)
											}
										>
											<img src={caloriesIcon} alt="Icono calorías" />
											<p>
												<span className={styles.subtitle}>Energía total:</span>{" "}
												{element.calories} calorías
											</p>
										</div>

										<div
											className={styles.macros}
											onClick={(e) =>
												navigate(`/dietas/info?dietId=${element.id}`)
											}
										>
											<div className={styles.macros_info}>
												<div className={styles.macros_item}>
													<img src={carbohidrato} alt="Icono carbs" />
													<p>Carbs</p>
												</div>
												<h5>{JSON.parse(element.macros).carbohidratos[0]}</h5>
												<div className={styles.bar}>
													<div
														className={styles.color_carbs}
														style={{
															width: `${(macros[0] / macros[3]) * 100}%`,
														}}
													></div>
												</div>
											</div>

											<div className={styles.macros_info}>
												<div className={styles.macros_item}>
													<img src={proteina} alt="Icono proteina" />
													<p>Proteina</p>
												</div>
												<h5>{JSON.parse(element.macros).proteina[0]}</h5>
												<div className={styles.bar}>
													<div
														className={styles.color_protein}
														style={{
															width: `${(macros[1] * 100) / macros[3]}%`,
														}}
													></div>
												</div>
											</div>

											<div className={styles.macros_info}>
												<div className={styles.macros_item}>
													<img src={grasas} alt="Icono grasas" />
													<p>Grasas</p>
												</div>
												<h5>{JSON.parse(element.macros).grasas[0]}</h5>
												<div className={styles.bar}>
													<div
														className={styles.color_fats}
														style={{
															width: `${(macros[2] / macros[3]) * 100}%`,
														}}
													></div>
												</div>
											</div>
										</div>
									</article>
								);
							})}
					</section> */}

					<div className={styles.btn_container}>
						<button
							onClick={() => {
								navigate("/dietas/favs");
							}}
							id={styles.ver_todo}
						>
							Ver todo
						</button>
					</div>
				</div>

				<div className={styles.dietas}>
					<h1>Buscar dietas</h1>

					<div className={styles.search_bar}>
						<div className={styles.aux}>
							{calories.length > 0 && (
								<Dropdown
									text={
										calorieFilter == 0 ? "Calorias" : calorieFilter.toString()
									}
									isOpen={caloriasOpen}
									setIsOpen={setCaloriasOpen}
								>
									<div className={styles.selection_calories}>
										<div
											onClick={() => {
												filterCalories(0);
											}}
											key={calories.length}
										>
											Default
										</div>
										{/* {calories.map((element: string, key: number) => {
											return (
												<div
													onClick={() => {
														filterCalories(Number.parseInt(element));
													}}
													key={key}
												>
													{element}
												</div>
											);
										})} */}
									</div>
								</Dropdown>
							)}
						</div>

						<div className={styles.aux}>
							<input
								onChange={(e) => {
									setIngredientFilter(e.target.value);
								}}
								type="text"
								name="alimentos"
								id="alimentos"
								placeholder="&#128269;  Buscar alimento"
							/>
						</div>
					</div>

					{/* <section>
						{diets.length === 0 && (
							<h2 style={{ marginBottom: `50px` }}>
								No hay dietas registradas
							</h2>
						)}
						{diets.length > 0 &&
							diets.map((element: any, key: number) => {
								return (
									<article className={styles.diet_card} key={key}>
										<div className={styles.titulo}>
											<h2
												onClick={(e) =>
													navigate(`/dietas/info?dietId=${element.id}`)
												}
											>
												Dieta {element.name}
											</h2>
											<img
												className={styles.favicon}
												onClick={(e) => {
													setDietStatusController(element.id);
												}}
												src={
													isFav.indexOf(element.id) !== -1
														? favicon
														: notFavicon
												}
												alt="Icono de !favoritos"
											/>
										</div>

										<div
											className={styles.diet_info}
											onClick={(e) =>
												navigate(`/dietas/info?dietId=${element.id}`)
											}
										>
											<div className={styles.macros_item}>
                                                <img src={caloriesIcon} alt="Icono calorías" />
                                                <p>{element.calories}</p>
                                            </div>

                                            <div className={styles.macros_item}>
                                                <img src={grasas} alt="Icono carbs" />
                                                <p>{JSON.parse(element.macros).grasas[0]}</p>
                                            </div>

                                            <div className={styles.macros_item}>
                                                <img src={proteina} alt="Icono carbs" />
                                                <p>{JSON.parse(element.macros).proteina[0]}</p>
                                            </div>

                                            <div className={styles.macros_item}>
                                                <img src={carbohidrato} alt="Icono carbs" />
                                                <p>{JSON.parse(element.macros).carbohidratos[0]}</p>
                                            </div>
										</div>
									</article>
								);
							})}
					</section> */}
				</div>
			</div>
		</Dashboard>
	);
}

export default Dietas;
